#include   <nik/string.hpp>  #inlcude <iostream>  int   main ()   { 
     auto   hello   =   nik :: string ( "hello" ); 
     std :: cout   <<   hello . capitalize ()   <<   std :: endl ;   // prints "Hello"  }

#include   <nik/string.hpp>  #inlcude <iostream>  int   main ()   { 
     auto   hello   =   nik :: string ( "hello" ); 
     std :: cout   <<   hello . center ( 9 )   <<   std :: endl ;        // prints "  hello  " 
     std :: cout   <<   hello . center ( 9 ,   '-' )   <<   std :: endl ;   // prints "--hello--" 
     std :: cout   <<   hello . center ( 1 )   <<   std :: endl ;        // prints "hello"  }

#include   <nik/string.hpp>  #inlcude <iostream>  int   main ()   { 
     auto   hello   =   nik :: string ( "hello hello hello" ); 
     std :: cout   <<   hello . count ( "hello" )   <<   std :: endl ;          // prints 3 
     std :: cout   <<   hello . count ( "hello" ,   5 )   <<   std :: endl ;       // prints 2 
     std :: cout   <<   hello . count ( "hello" ,   5 ,   12 )   <<   std :: endl ;   // prints 1  }

#include   <nik/string.hpp>  #include   <cassert>  #include   <iostream>  int   main ()   { 
     // splitting on a comma 
     auto   csv   =   nik :: string ( "a,b,c,d,e" ); 

     auto   csv_toks   =   csv . split ( "," ); 
     assert ( csv_toks . size ()   ==   5 ); 

     auto   csv_3toks   =   csv . split ( "," ,   2 ); 
     assert ( csv_3toks . size ()   ==   3 ); 

     // splitting on whitespace 
     auto   text   =   nik :: string ( "a     \t  b  \t\t\t  c" ); 
     assert ( text . split (). size ()   ==   3 );  }
